    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Connexion Admin - GeniusPro360°{% endblock %}

{% block stylesheets %}
    {{ parent() }}
    <link href=\"https://cdn.jsdelivr.net/npm/bootstrap@5.3.2/dist/css/bootstrap.min.css\" rel=\"stylesheet\">
{% endblock %}

{% block body %}
<div class=\"container d-flex align-items-center justify-content-center\" style=\"min-height: 100vh;\">
    <div class=\"card shadow p-4\" style=\"width: 100%; max-width: 400px;\">
        <h2 class=\"text-center mb-4\">Espace Admin</h2>

        {% if error %}
            <div class=\"alert alert-danger\">
                {{ error.messageKey|trans(error.messageData, 'security') }}
            </div>
        {% endif %}

        <form method=\"post\" action=\"{{ path('admin_login') }}\">
            <div class=\"mb-3\">
                <label for=\"username\" class=\"form-label\">Email</label>
                <input type=\"text\" id=\"username\" name=\"_username\" class=\"form-control\" required autofocus>
            </div>

            <div class=\"mb-3\">
                <label for=\"password\" class=\"form-label\">Mot de passe</label>
                <input type=\"password\" id=\"password\" name=\"_password\" class=\"form-control\" required>
            </div>

            <input type=\"hidden\" name=\"_csrf_token\" value=\"{{ csrf_token('authenticate') }}\">

            <button type=\"submit\" class=\"btn btn-primary w-100\">Connexion</button>
        </form>

        <div class=\"text-center mt-3\">
            <a href=\"{{ path('app_home') }}\" class=\"btn btn-link\">← Retour au site</a>
        </div>
    </div>
</div>
{% endblock %}

{% block javascripts %}
    {{ parent() }}
{% endblock %}
